INSERT INTO `Products` (`productId`, `name`, `price`, `des`, `type`, `url1`, `url2`, `url3`, `url4`, `rate`, `rateQuantity`) VALUES
(1, 'MacBook Air 2020 M1 256GB', '28999000', 'Màn hình siêu rộng như máy tính bảng khi mở.<br>Đa nhiệm vượt trội, mạnh mẽ dẫn đầu.<br>Thách thức giới hạn với khả năng gập mở linh hoạt', 1, 'https://salt.tikicdn.com/cache/400x400/media/catalog/producttmp/e6/30/f3/f82bda03945cc1907614da72f523e76c.jpg.webp', 'https://salt.tikicdn.com/cache/400x400/media/catalog/producttmp/08/5a/c3/0048bf2ccc571bccb5ddafdfade5cbcb.jpg.webp', 'https://salt.tikicdn.com/cache/400x400/media/catalog/producttmp/6f/8c/1b/3d05be1255cd45f88bd91097b45fad92.jpg.webp', 'https://salt.tikicdn.com/cache/400x400/media/catalog/producttmp/c1/73/67/fc00371c2a5e7813c6eaae1790a09d42.jpg.webp', 0, 0),
(2, 'MacBook Air 2019 – i5 128GB', '20500000', 'Bảng màu đa dạng, trẻ trung.<br>Màn hình tần số quét 120Hz siêu mượt.<br>Cụm 3 camera chuyên nghiệp với Space Zoom 30x', 1, 'https://macbook.haloshop.vn/image/cache/catalog/products/apple/macbook/macbook-air-2018-gold-00-700x700.jpg', 'https://macbook.haloshop.vn/image/cache/catalog/products/apple/macbook/macbook-air-2018-41-700x700.jpg', 'https://macbook.haloshop.vn/image/cache/catalog/products/apple/macbook/macbook-air-2018-42-700x700.jpg', 'https://macbook.haloshop.vn/image/cache/catalog/products/apple/macbook/macbook-air-2018-43-700x700.jpg', 0, 0),
(3, 'MacBook Air 2019 – i5 256GB', '22500000', 'Quay video với chất lượng đỉnh cao chưa từng có.<br>Màn hình thông minh bậc nhất từ trước đến nay.<br>Chip mạnh mẽ nhất trên các dòng thiết bị Galaxy', 1, 'https://macbook.haloshop.vn/image/cache/catalog/products/apple/macbook/macbook-air-2018-gold-00-700x700.jpg', 'https://macbook.haloshop.vn/image/cache/catalog/products/apple/macbook/macbook-air-2018-41-700x700.jpg', 'https://macbook.haloshop.vn/image/cache/catalog/products/apple/macbook/macbook-air-2018-42-700x700.jpg', 'https://macbook.haloshop.vn/image/cache/catalog/products/apple/macbook/macbook-air-2018-43-700x700.jpg', 0, 0),
(4, 'MacBook Air 2018 Max Option', '34500000', 'Từ 15-31/05, giảm 5M-Giảm 50% khi mua bao da kèm S-Pen.<br>Độ phân giải cao nhất tìm thấy trên điện thoại.<br>Khả năng hiển thị sống động, rực rỡ nhất từng thấy', 1, 'https://laptopvang.com/wp-content/uploads/2018/10/dasdas.jpg', 'https://laptopvang.com/wp-content/uploads/2018/10/MacBook-Air-2018-Gray.jpg', 'https://laptopvang.com/wp-content/uploads/2018/10/Co%CC%82%CC%89ng-ke%CC%82%CC%81t-no%CC%82%CC%81i-MacBook-AIr-2018.jpg', 'https://laptopvang.com/wp-content/uploads/2018/10/Ma%CC%80n-hi%CC%80nh-MacBook-Air-2018.jpg', 0, 0),
(5, 'MacBook Air 2018 – i5 128GB', '19500000', 'Camera chuyên nghiệp với video 8k, độ phân giải cao 64MP và Space Zoom 30 lần.<br>Bảo mật Samsung Knox, pin thông minh, bộ xử lý mạnh mẽ và dung lượng lưu trữ lớn.<br>Tốc độ mạnh mẽ giúp bạn chia sẻ và truyền phát trực tuyến', 1, 'https://laptopvang.com/wp-content/uploads/2018/10/MacBook_Air_2018_m%C3%A0u_Gold-768x714.jpg', 'https://laptopvang.com/wp-content/uploads/2018/11/macbook-air-2018-gold-00-700x700-600x600.jpg', 'https://laptopvang.com/wp-content/uploads/2018/11/macbook-air-2018-41-700x700-600x600.jpg', 'https://laptopvang.com/wp-content/uploads/2018/11/macbook-air-2018-42-700x700-600x600.jpg', 0, 0),
(6, 'Macbook Air MQD32 (13.3 inch, 2017 - Realse 2020 )', '16500000', 'Màn hình 6.1\" Quad HD+.<br>Dung lượng pin 3,400mAh', 1, 'https://mac24h.vn/images/companies/1/Macbook%20Air/mqd42/MQD32.jpg?1634188201981', 'https://mac24h.vn/images/companies/1/Macbook%20Air/mqd42/MQD32%204.jpg?1634188183874', 'https://mac24h.vn/images/companies/1/Macbook%20Air/mqd42/D32%2010.jpg?1634188549688', 'https://mac24h.vn/images/companies/1/Macbook%20Air/mqd42/mqd322.jpg?1634188220906', 0, 0),
(7, 'MacBook Pro 14 inch 2021 RAM 32GB', '106000000', 'MacBook Pro 14 inch 2021 với thiết kế được Apple thay đổi toàn bộ cùng với cấu hình mạnh mẽ đến từ Apple M1 Pro cùng 32GB RAM và 1TB SSD sẽ là một lựa chọn tuyệt vời dành cho bạn nếu bạn đang tìm một chiếc máy có thể đáp ứng tốt các công việc của mình.', 2, 'https://laptopvang.com/wp-content/uploads/2021/10/MacBook_Pro_14_inch_2021_laptopvang-3-768x768.png', 'https://laptopvang.com/wp-content/uploads/2021/10/MacBook_Pro_14_inch_2021_laptopvang-4-768x768.png', 'https://laptopvang.com/wp-content/uploads/2021/10/MacBook_Pro_14_inch_2021_laptopvang-2-768x768.png', 'https://laptopvang.com/wp-content/uploads/2021/10/MacBook_Pro_14_inch_2021_laptopvang-1-768x768.png', 0, 0),
(8, 'MacBook Pro 2020 13 inch', '29000000', 'Apple vừa ra mắt chiếc Macbook Pro MYDA2 chạy chip M1 vào tháng 11 – Apple nói rằng đây là chiếc Macbook với hiệu năng mạnh mẽ của họ. Liệu đây có phải là sản phẩm đáng tiền? Hãy cùng Laptop Vàng đi sâu hơn về thông tin chi tiết của máy nhé.', 2, 'https://laptopvang.com/wp-content/uploads/2020/11/M1SIL.png', 'https://laptopvang.com/wp-content/uploads/2020/11/mbp-silver-2020-m1-3-768x714.jpeg', 'https://laptopvang.com/wp-content/uploads/2020/11/mbp-silver-2020-m1-1.jpg', 'https://laptopvang.com/wp-content/uploads/2020/11/mbp-spacegray-2020-m1-1.jpeg', 0, 0),
(9, 'MacBook Pro 14 inch 2021', '72000000', 'MacBook Pro 14 inch 2021 MKGQ3 với thiết kế được Apple thay đổi toàn bộ cùng với cấu hình mạnh mẽ đến từ Apple M1 Pro cùng 16GB RAM và 1TB SSD sẽ là một lựa chọn tuyệt vời dành cho bạn nếu bạn đang tìm một chiếc máy có thể đáp ứng tốt các công việc của mình.', 2, 'https://laptopvang.com/wp-content/uploads/2021/10/MBP-2021-768x768.png', 'https://laptopvang.com/wp-content/uploads/2021/10/MacBook_Pro_14_inch_2021_laptopvang-4-768x768.png', 'https://laptopvang.com/wp-content/uploads/2021/10/MacBook_Pro_14_inch_2021_laptopvang-2-768x768.png', 'https://laptopvang.com/wp-content/uploads/2021/10/MacBook_Pro_14_inch_2021_laptopvang-1-768x768.png', 0, 0),
(10, 'MacBook Pro 2019 15 inch', '37000000', 'Với một loạt Macbook Pro 2019 được cho ra mắt trong năm nay Apple đã từng bước chinh phục người dùng. Trong số các dòng sản phẩm Macbook không thể bỏ qua Macbook Pro MV902 .\r\nĐây là dòng sản phẩm tiên tiến nhất hiện nay cả  về cấu hình, chức năng lẫn thiết kế sang trọng và đẳng cấp. Để hiểu sâu hơn về dòng sản phẩm này. Laptopvang sẽ cung cấp đến bạn những thông tin cụ thể nhất về  Macbook MV902 15 Inch này nhé!', 2, 'https://laptopvang.com/wp-content/uploads/2019/05/macbook-pro-15-inch-space-gray-laptopvang.com_.jpg', 'https://laptopvang.com/wp-content/uploads/2018/07/2-768x590.jpg', 'https://laptopvang.com/wp-content/uploads/2018/07/4-768x590.jpg', 'https://laptopvang.com/wp-content/uploads/2018/07/3-768x590.jpg', 0, 0),
(11, 'iMac 2020 27 inch 5K', '44000000', 'iMac 2020 đã ra mắt với những cải tiến không nằm ở thiết kế mà sẽ nằm ở cấu hình nhằm nâng cấp trải nghiệm của người dùng. Ở bài viết này laptopVANG sẽ nói chi tiết hơn về phiên bản iMac 2020 27 inch MXWU2 nhé', 3, 'https://laptopvang.com/wp-content/uploads/2020/08/imac_21.5_inch_2020_laptopavang-3-1.jpeg', 'https://laptopvang.com/wp-content/uploads/2020/08/imac_2020_laptopavang-2.jpeg', 'https://laptopvang.com/wp-content/uploads/2020/08/imac_21.5_inch_2020_laptopavang-4.jpeg', 'https://laptopvang.com/wp-content/uploads/2020/08/imac_21.5_inch_2020_laptopavang-1.jpeg', 0, 0),
(12, 'iPad Air 4 10.9 inch 2020 (4G)', '19000000', 'ăm nay iPad Air 4 chính thức thay đổi thiết kế sau gần 6 năm. Thiết kế giờ đây tương tự như mẫu iPad Pro 2020 vẫn thiết kế nhôm nguyên khối nhưng các góc đã làm vuông vức thay vì bo tròn như trước\r\n<br>\r\nTouchID phía trước cũng được bỏ đi và chuyển sang vị trí khác và thay vào đó là màn hình lớn hơn với 10.9 inch cùng kích thước 247.6 x 178.5 mm và độ mỏng chỉ 6.1 mm.\r\n<br>\r\nNgoài ra năm nay iPad Air 4 sẽ bao gồm 5 phiên bản màu sắc khác nhau và cũng sẽ có 2 phiên bản là WiFi và LTE', 4, 'https://laptopvang.com/wp-content/uploads/2021/06/ipad_Air_4_silver_laptopvang-3-1.jpeg', 'https://laptopvang.com/wp-content/uploads/2021/06/ipad_Air_4_silver_laptopvang-1.jpeg', 'https://laptopvang.com/wp-content/uploads/2021/06/ipad_Air_4_silver_laptopvang-2.jpeg', 'https://laptopvang.com/wp-content/uploads/2021/06/ipad_Air_4_silver_laptopvang-3-1.jpeg', 4, 1),
(13, 'iPad Air 4 10.9 inch 2020 (Wifi Only)', '15500000', 'iPad Air 4 chiếc Tablet thuộc dòng Air mới nhất của Apple được ra mắt với rất nhiều sự cải tiến với phiên bản tiền nhiệm và nó được xem là chiếc iPad tốt nhất hiện nay cả về giá và những gì mà nó đem lại. Vậy iPad Air 4 có gì mà khiến người dùng bây giờ phải cân nhắc nó với một chiếc iPad Pro 11 inch vốn hơn 1 bậc hãy cùng LaptopVANG tìm hiểu nhé!', 4, 'https://laptopvang.com/wp-content/uploads/2021/06/ipad_Air_4_silver_laptopvang-3-1.jpeg', 'https://laptopvang.com/wp-content/uploads/2021/06/ipad_Air_4_silver_laptopvang-1.jpeg', 'https://laptopvang.com/wp-content/uploads/2021/06/ipad_Air_4_silver_laptopvang-2.jpeg', 'https://laptopvang.com/wp-content/uploads/2021/06/ipad_Air_4_silver_laptopvang-3-1.jpeg', 0, 0),
(14, 'HyperDrive 6-in-11 For iPad Pro', '1500000', 'HyperDrive 6 in 1 dành cho iPad Pro với một thiết kế đẹp, hoàn thiện cao cấp, kết nối ổn định.\r\n<br>\r\nVới thiết kế mới trên iPad Pro 2018 khi Apple quyết định trang bị cổng Type C để thay thế cổng Lightning truyền thống với tham vọng có thể biến iPad thành một chiếc máy có thể làm việc chứ không đơn thuần là giải trí', 5, 'https://laptopvang.com/wp-content/uploads/2020/03/hyper-drive-6in1-laptopvang.com_.jpg', 'https://laptopvang.com/wp-content/uploads/2020/03/ports-hyper-drive-6in1-laptopvang.com-2.jpg', 'https://laptopvang.com/wp-content/uploads/2020/03/ports-hyper-drive-6in1-laptopvang.com_.jpg', 'https://laptopvang.com/wp-content/uploads/2020/03/hyper-drive-6in1-laptopvang.com_.jpg', 5, 1),
(15, 'iPad Mini 2 Wifi', '3490000', 'iPad Mini 2 Wifi sở hữu một màn hình Retina tuyệt vời độ phân giải 2048 x 1536 pixel, mỗi hình ảnh hiện ra đều chi tiết và sống động, mỗi chữ trong văn bản hay trên trang web đều sắc nét, rõ ràng. Với độ phân giải vượt trội so với những chiếc TV HD tiên tiến trên thị trường, iPad Mini 2 Wifi như một bức tranh tuyệt đẹp, cho bạn cảm giác chân thực hơn bao giờ hết.', 6, 'https://didongmango.com/images/products/2019/07/10/original/ipad-mini-2-retina-cellular-16gb-01_1562736454.jpg.jpg', 'https://didongmango.com/images/products/2019/07/10/original/ipad-mini-2-retina-cellular-16gb-03_1562736454.jpg.jpg', 'https://didongmango.com/images/products/2019/07/10/original/ipad-mini-2-retina-cellular-16gb-02_1562736454.jpg.jpg', 'https://didongmango.com/images/products/2019/07/10/original/ipad-mini-2-retina-cellular-16gb-02_1562736454.jpg.jpg', 0, 0),
(16, 'iPhone 13 Pro Max', '32000000', 'iPhone 12 ra mắt cách đây chưa lâu, thì những tin đồn mới nhất về iPhone 13 Pro Max đã khiến bao tín đồ công nghệ ngóng chờ. Cụm camera được nâng cấp, màu sắc mới, đặc biệt là màn hình 120Hz với phần notch được làm nhỏ gọn hơn chính là những điểm làm cho thu hút mọi sự chú ý trong năm nay.', 7, 'https://cdn.cellphones.com.vn/media/catalog/product/cache/7/image/1000x/040ec09b1e35df139433887a97daa66f/i/p/iphone_13-_pro-4_2.jpg', 'https://cdn.cellphones.com.vn/media/catalog/product/cache/7/image/1000x/040ec09b1e35df139433887a97daa66f/i/p/iphone_13-_pro-5_4.jpg', 'https://cdn.cellphones.com.vn/media/catalog/product/cache/7/image/1000x/040ec09b1e35df139433887a97daa66f/i/p/iphone_13-_pro-3_2.jpg', 'https://cdn.cellphones.com.vn/media/catalog/product/cache/7/image/1000x/040ec09b1e35df139433887a97daa66f/i/p/iphone_13-_pro-4_2.jpg', 0, 0),
(17, 'Apple iPhone 12 - 64GB', '18450000', 'Vào ngày 13/10 vừa qua, Apple đã chính thức cho ra mắt thế hệ iPhone 12 mới với 4 phiên bản, và trong đó mẫu iPhone 12 tiêu chuẩn, với nhiều cải tiến về cả bên ngoài lẫn bên trong, hứa hẹn sẽ lại tiếp tục nối tiếp sự thành công từ chiếc iPhone 11 tiền nhiệm.', 8, 'https://didongviet.vn/pub/media/catalog/product//i/p/iphone-12-pro-256gb-chinh-hang-vna-troi-bao-hanh-didongviet.jpg', 'https://cdn.hoanghamobile.com/i/preview/Uploads/2020/11/06/apple-iphone-12-mini-3.png', 'https://cdn.hoanghamobile.com/i/preview/Uploads/2021/04/22/image-removebg-preview_637547045799326930.png', 'https://cdn.hoanghamobile.com/i/preview/Uploads/2021/04/22/image-removebg-preview_637547045799326930.png', 0, 0),
(18, 'iPhone 11 Pro 64GB', '17390000', 'iPhone 11 Pro 64GB cũ có thiết kế cao cấp sang trọng, đi kèm với đó là vi xử lý A13 Bionic mạnh mẽ cùng hệ thống ba camera sau 12MP hứa hẹn sẽ mang lại cho người dùng những trải nghiệm vô cùng mượt mà.\r\n<br>\r\niPhone 11 Pro 64GB cũ chính hãng là bản quốc tế bỏ sim vào là dùng được ngay. Máy được Di Động Việt bán ra với mức giá ưu đãi và bảo hành 06 tháng, hỗ trợ 1 đổi 1 và nhiều chương trình khác.', 9, 'https://didongviet.vn/pub/media/catalog/product//i/p/iphone-11-pro-xanh-didongviet.jpg', 'https://didongviet.vn/pub/media/catalog/product//i/p/iphone-11-pro-64gb-likenew2.jpg', 'https://didongviet.vn/pub/media/catalog/product//i/p/iphone-11-pro-64gb-likenew1.jpg', 'https://didongviet.vn/pub/media/catalog/product//i/p/iphone-11-pro-xanh-didongviet.jpg', 0, 0),
(19, 'Apple Watch Series 7 4G', '20490000', 'Apple Watch Series 7 là thế hệ smartwatch mới được Apple ra mắt trong tháng 9 này, nối tiếp sự thành công của Series 6. Với những đổi mới về thiết kế và tính năng, Apple Watch Series 7 sẽ là một trong những chiếc smartwatch thịnh hành nhất trong thời gian sắp tới. Dưới đây là thông tin chi tiết sản phẩm.', 10, 'https://cdn.hoanghamobile.com/i/preview/Uploads/2021/10/08/s7-thep-2.png', 'https://cdn.hoanghamobile.com/i/preview/Uploads/2021/10/08/s7-thep-1.png', 'https://cdn.hoanghamobile.com/i/preview/Uploads/2021/10/08/s7-thep-3.png', 'https://cdn.hoanghamobile.com/i/preview/Uploads/2021/10/08/s7-thep-2.png', 0, 0),
(20, 'Apple Watch SE GPS', '7890000', 'Apple Watch SE 44mm GPS – một trong những sản phẩm đồng hồ thông minh mới vừa được Apple ra mắt, với mức giá rẻ cùng với nhiều tính năng cao cấp, sản phẩm này hứa hẹn sẽ tạo ra một sức hút sẽ không thua kém gì phiên bản cao cấp.', 11, 'https://cdn.hoanghamobile.com/i/preview/Uploads/2021/01/12/se-silver.png', 'https://cdn.hoanghamobile.com/i/preview/Uploads/2021/01/12/se-gols.png', 'https://cdn.hoanghamobile.com/i/preview/Uploads/2021/01/12/se-gray.png', 'https://cdn.hoanghamobile.com/i/preview/Uploads/2021/01/12/se-silver.png', 0, 0),
(21, 'iPhone 12 256GB Chính hãng', '22590000', 'iPhone 12 256GB Chính hãng (VN/A) bán tại Di Động Việt - Đại lý uỷ quyền chính thức của Apple tại Việt Nam, là phiên bản quốc tế 2 sim (Nano + Esim) chính hãng VN/A. Máy chưa Active + nguyên seal hộp, mới 100% (Fullbox)\r\niPhone 12 256GB Chính hãng (VN/A) là phiên bản được phân phối chính thức bởi Apple Việt Nam, được bảo hành 12 tháng tại Trung tâm Uỷ quyền cao cấp nhất của Apple tại Việt Nam và trên toàn cầu miễn phí. Đồng thời hưởng nhiều ưu đãi, khuyến mãi hấp dẫn tại Di Động Việt.', 8, 'https://didongviet.vn/pub/media/catalog/product//i/p/iphone-12-didongviet-mau-xanh-la_3.jpg', 'https://didongviet.vn/pub/media/catalog/product//i/p/iphone-12-hinh-anh1_3.jpg', 'https://didongviet.vn/pub/media/catalog/product//i/p/iphone-12-hinh-anh2_4.jpg', 'https://didongviet.vn/pub/media/catalog/product//i/p/iphone-12-didongviet-mau-xanh-la_3.jpg', 0, 0),
(22, 'iPhone 11 128GB', '13990000', 'iPhone 11 128GB cũ sở hữu thiết kế thời thượng, nhiều màu sắc, cấu hình mạnh mẽ với chip A13 Bionic. iPhone 11 128GB mang lại sự trải nghiệm tốt cho người dùng thoả sức sử dụng.\r\niPhone 11 128GB cũ hàng chính hãng là phiên bản quốc tế chỉ cần bỏ sim là dùng được. Sản phẩm được bán ra tại Di Động Việt với mức giá ưu đãi và bảo hành 6 tháng, hỗ trợ 1 đổi 1.', 9, 'https://didongviet.vn/pub/media/catalog/product//a/p/apple-iphone-11-tim-didongviet_3.jpg', 'https://didongviet.vn/pub/media/catalog/product//i/p/iphone-11-128gb-cu-like-new-mau-tim.jpg', 'https://didongviet.vn/pub/media/catalog/product//i/p/iphone-11-128gb-cu-like-new-mau-do-red.jpg', 'https://didongviet.vn/pub/media/catalog/product//a/p/apple-iphone-11-tim-didongviet_3.jpg', 0, 0),
(23, 'MacBook Pro 13\" 2020 Touch Bar M1 256GB', '33999000', 'Apple M1 chip with 8-core CPU, 8-core GPU, and 16-core Neural Engine\r\n8GB unified memory\r\n256GB SSD storage¹\r\n13-inch Retina display with True Tone\r\nMagic Keyboard\r\nTouch Bar and Touch ID\r\nForce Touch trackpad\r\nTwo Thunderbolt / USB 4 ports', 2, 'https://cdn.tgdd.vn/Products/Images/44/236131/apple-macbook-pro-m1-2020-z11c000cj-600x600.jpg', 'https://laptopvang.com/wp-content/uploads/2017/06/macbook-pro-2016-13-inch-00-700x700.jpg', 'https://bizweb.dktcdn.net/100/318/659/products/5919-637408000895490753-mbp-2020-m1-gray-2-png-jpeg-2620a626-189f-48cd-b6c5-00313eb56d78.jpg?v=1605773672390', 'https://bizweb.dktcdn.net/thumb/1024x1024/100/318/659/products/mbp13-s2018-final-b9eea4d0-1a94-4ed2-8547-ed93706d79ff.jpg?v=1541405485077', 0, 0),
(24, 'iMac 2020 27 inch 5K', '41000000', 'Với những kì vọng ở một chiếc iMac mới chạy chip “cây nhà lá vườn” ARM của Apple có lẽ vẫn chưa thành hiện thực. Đầu tháng 8 này, nhà Táo đã bất ngờ tung ra Apple iMac 2020 với những sự thay đổi không nằm ở ngoại hình mà là ở cấu hình. Ở bài viết này, LaptopVANG sẽ nói về phiên bản tiêu chuẩn nhất của iMac 2020 27 inch 5K MXWT2.', 3, 'https://laptopvang.com/wp-content/uploads/2020/08/imac_21.5_inch_2020_laptopavang-3-1.jpeg', 'https://laptopvang.com/wp-content/uploads/2020/08/imac_2020_laptopavang-2.jpeg', 'https://laptopvang.com/wp-content/uploads/2020/08/imac_21.5_inch_2020_laptopavang-4.jpeg', 'https://laptopvang.com/wp-content/uploads/2020/08/imac_21.5_inch_2020_laptopavang-5.jpeg', 0, 0),
(25, 'Apple Watch Series 7 GPS + Cellular 45mm', '21990000', 'Apple Watch Series 7 GPS + Cellular phiên bản làm bằng chất liệu thép cao cấp đầy sang trọng chính là sự kết hợp hoàn hảo giữa công nghệ và thời trang. Chiếc đồng hồ tỏa sáng ngay trên cổ tay của bạn không chỉ từ thiết kế tuyệt mỹ mà còn đến từ những tính năng thông minh đỉnh cao.', 10, 'https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/apple-watch-7-steel-2.jpg', 'https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/apple-watch-7-steel-5.jpg', 'https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/apple-watch-7-14.jpg', 'https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/apple-watch-7-3.jpg', 0, 0),
(26, 'iMac 4K 21nch (Model 2019, Realeased 2020)', '30400000', 'Phiên bản iMac 21 inch 2020 , iMac 27 inch 2020 có giá khởi điểm lần lượt là 1099 USD và 1.790 USD , người dùng có thể đặt hàng ngay từ hôm nay. Apple nâng trải nghiệm của người dùng iMac thêm tốt hơn với độ sáng màn hình 500nits trên màn hình 5K, lần đầu tiên công nghệ True tone vối đã quá nổi tiếng và hay ho Apple đã được trang bị trên iMac không những vậy, công nghệ chống chói của Apple trên siêu phẩm Pro Dislay XDR cũng đã xuất hiện nó giúp người dùng dù thay đổi môi trường làm việc, hay ánh sáng chiếu vào màn hình sẽ ít bị phản chiếu ngược lại duy trì độ tương phản tốt và chất lượng hình ảnh cũng tốt hơn, bạn xem hình minh hoạ ánh sáng dưới đây', 3, 'https://mac24h.vn/images/thumbnails/350/350/detailed/48/IMAC_21_INCH_2020_MAC24H.png?t=1629387052', 'https://mac24h.vn/images/thumbnails/350/350/detailed/48/IMAC_21_INCH_2020_MAC24H.png?t=1629387052', 'https://mac24h.vn/images/thumbnails/350/350/detailed/48/IMAC_21_INCH_2020_MAC24H.png?t=1629387052', 'https://mac24h.vn/images/thumbnails/350/350/detailed/48/IMAC_21_INCH_2020_MAC24H.png?t=1629387052', 0, 0),
(27, 'iPhone 13 512GB Chính Hãng', '28990000', 'iPhone 13 512GB Chính hãng (VN/A) bán tại Di Động Việt - Đại lý uỷ quyền chính thức của Apple tại Việt Nam, iPhone 13 mini là phiên bản quốc tế 2 sim (Nano + Esim) chính hãng VN/A. Máy chưa Active + nguyên seal hộp, mới 100% (Fullbox)\r\n\r\niPhone 13 512GB Chính hãng (VN/A) là phiên bản được phân phối chính thức bởi Apple Việt Nam, được bảo hành 12 tháng tại Trung tâm Uỷ quyền cao cấp nhất của Apple tại Việt Nam và trên toàn cầu miễn phí. Đồng thời hưởng nhiều ưu đãi, khuyến mãi hấp dẫn tại Di Động Việt', 7, 'https://didongviet.vn/pub/media/catalog/product//i/p/iphone-13-red-9-didongviet_2.jpg', 'https://didongviet.vn/pub/media/catalog/product//i/p/iphone-13-red-10-didongviet_2.jpg', 'https://didongviet.vn/pub/media/catalog/product//i/p/iphone-13-red-11-didongviet_2.jpg', 'https://didongviet.vn/pub/media/catalog/product//i/p/iphone-13-red-9-didongviet_2.jpg', 0, 0),
(28, 'iMac 4K 21nch (Model 2019, Realeased 2020) - Quad Core I5 3.0 8GB 256GB RP 560X 4GB', '24500000', 'Ở phiên bản nâng cấp này, Apple họ đã trang bị chip xử lý intel thế hệ 10 của mình Comet Lake và một Webcam có độ phân giải tốt hơn độ phân giải 1080 so với 720 đối với thế hệ trước. Ổ cứng SSD cũng sẽ là trang bị cơ bản đối với các dòng máy của năm 2020, một trang bị khá thú vị là Option màn hình ít phản chiếu hơn \" nano-texture \".', 3, 'https://mac24h.vn/images/companies/1/iMac/imac%2027%20inch%202020/imac-2020-21.jpg?1596736391770', 'https://mac24h.vn/images/companies/1/iMac/imac%2027%20inch%202020/imac-2020-21.jpg?1596736391770', 'https://mac24h.vn/images/companies/1/iMac/imac%2027%20inch%202020/imac-2020-21.jpg?1596736391770', 'https://mac24h.vn/images/companies/1/iMac/imac%2027%20inch%202020/imac-2020-21.jpg?1596736391770', 0, 0),
(29, 'Apple Watch Series 7 45mm (4G)', '20990000', 'Apple Watch Series 7 cho thời gian sạc nhanh hơn\r\nTrong sự kiện giới thiệu Apple Watch Series 7, Apple cũng đã công bố rằng sản phẩm này sẽ có thời lượng pin vào khoảng 18 tiếng. Theo mình đây là một mức dung lượng ở mức ổn, có thể sử dụng gần cả ngày mới phải sạc lại. Tuy không cải tiến về thời lượng pin nhưng Apple đã nâng cấp khả năng sạc nhanh của Apple Watch Series 7 lên 33%, sạc từ 0 lên 80% pin trong 45 phút hay chỉ với 8 phút sạc bạn sẽ có thể sử dụng được trong 8 giờ. Dù chưa phải là giải pháp tối ưu nhất để cải thiện thời lượng pin trên Apple Watch Series 7 nhưng việc cải tiến thời gian sạc của Apple vẫn được mình đánh giá cao và là một giải pháp có thể chấp nhận được ở thời điểm hiện tại.\r\n\r\n', 10, 'https://cdn.techzones.vn/Data/Sites/1/Product/32045/techzones-apple-watch-series-7-4g-vien-thep-bac-day-cao-su-trang-1.jpg', 'https://cdn.techzones.vn/Data/Sites/1/Product/32045/techzones-apple-watch-series-7-4g-vien-thep-bac-day-cao-su-trang-2.jpg', 'https://cdn.techzones.vn/Data/Sites/1/Product/32045/techzones-apple-watch-series-7-4g-vien-thep-bac-day-cao-su-trang.jpg', 'https://cdn.techzones.vn/Data/Sites/1/Product/32045/techzones-apple-watch-series-7-4g-vien-thep-bac-day-cao-su-trang-1.jpg', 0, 0),
(30, ' iMac 24 inch 4.5K - 2021 - Apple M1【8 Core CPU | 7 Core GPU】16GB 1TB SSD', '49500000', 'Được ra mắt vào tháng 4 năm 2021, IMac M1 24 inch chắc chắn sẽ là lựa chọn của đại đa số người dùng, nó cung cấp quá đủ tính năng quan trọng hàng ngày như màn hình chất lượng cao, Camera, Micro và hệ thống loa như Studio chuyên nghiệp. Thay vào sử dụng thế hệ chip Intel như trước đây, nay Apple đã chính thức sử dụng cây nhà trông được Apple M1 Chip và sản phẩm iMac mới nhất của mình.', 3, 'https://vcdn-sohoa.vnecdn.net/2021/04/21/m1-imac-2021-1618969614-8311-1618970163.jpg', 'https://vcdn-sohoa.vnecdn.net/2021/04/21/m1-imac-2021-1618969614-8311-1618970163.jpg', 'https://vcdn-sohoa.vnecdn.net/2021/04/21/m1-imac-2021-1618969614-8311-1618970163.jpg', 'https://vcdn-sohoa.vnecdn.net/2021/04/21/m1-imac-2021-1618969614-8311-1618970163.jpg', 0, 0),
(31, 'Apple Watch SE 44', '7990000', 'Thiết kế thời thượng cùng màn hình cực chất lượng\r\nApple Watch SE 40mm GPS mang trên mình một thiết kế thời thượng, cực kì năng động và cực kì cá tính, đậm chất thời trang như các thế hệ Apple Watch trước đây mà “Táo khuyết” đã dày công thiết kế. Đặc biệt, chất liệu dây đeo của Apple Watch SE được hoàn thiện từ chất liệu silicone dẻo, giúp dây đeo này có độ đàn hồi tốt, nhờ đó người dùng có thể đeo thiết bị trong cả một ngày dài mà không lo bị mỏi, vướng hoặc đau tay. Và chất liệu silicone này cũng sẽ giúp cho người dùng có thể dễ dàng vệ sinh cũng như hạn chế bụi bẩn bám vào dây đeo\r\nApple Watch SE 40mm GPS được trang bị màn hình Retina LTPO OLED độ phân giải 394 x 324 pixel, tượng tự với những gì mà Apple Watch Series 6 sở hữu với kích thước là 40mm x 34mm x 10.4mm. Với kích thước màn hình rộng như vậy kèm theo với độ phân giải cao, công nghệ màn hình tiến tiến, Apple Watch SE chăc chắn sẽ mang lại cho người dùng nhưng trải nghiệm hình ảnh chất lượng cao, rõ ràng, sắc nét, màu sắc tự nhiên và chân thực.\r\n\r\nHiệu năng mượt mà với vi xử lý Apple S5\r\nApple Watch SE 40mm GPS được Apple trang bị bộ vi xử lý mạnh mẽ S5 lõi kép, mang tới một hiệu suất xử lý cực kì mượt mà và ổn định. Đi cùng với vi xử lý S5 là thanh RAM 1GB và 32GB ROM, khiến cho những tác vụ đa nhiệm cùng với nhu cầu lưu trữ dữ liệu của người dùng sẽ trở nên thoải mái hơn rất nhiều.Đăc biệt, Apple Watch SE còn được tích hợp một tính năng đo tiếng ồn và phát hiện khả năng người dùng bị té ngã. Thiết bị sẽ phát ra một báo động khẩn cấp nếu như phát hiện thấy chủ nhân của chúng có nguy cơ bị ngã và không cử động được trong một khoảng thời gian nhất định.\r\n\r\nNgoài ra, Apple Watch SE 40mm GPS cũng có khả năng “bơi lặn” cực tốt khi được trang bị chuẩn chống nước 5 ATM, giúp thiết bị có thể sống được ở độ sâu 50m dưới nước trong khoảng thời gian là 30 phút.', 11, 'https://hoanghamobile.com/Uploads/2021/01/26/apple-watch-se-3.jpg', 'https://hoanghamobile.com/Uploads/2021/01/26/apple-watch-se_637472497992150119.jpg', 'https://hoanghamobile.com/Uploads/2021/01/26/apple-watch-see.jpg', 'https://hoanghamobile.com/Uploads/2021/01/26/apple-watch-se-2.jpg', 0, 0),
(32, 'iMac 24 inch 4.5K - 2021 - Apple M1【8 Core CPU | 7 Core GPU】16GB 256GB SSD', '38900000', 'Được ra mắt vào tháng 4 năm 2021, IMac M1 24 inch chắc chắn sẽ là lựa chọn của đại đa số người dùng, nó cung cấp quá đủ tính năng quan trọng hàng ngày như màn hình chất lượng cao, Camera, Micro và hệ thống loa như Studio chuyên nghiệp. Thay vào sử dụng thế hệ chip Intel như trước đây, nay Apple đã chính thức sử dụng cây nhà trông được Apple M1 Chip và sản phẩm iMac mới nhất của mình.', 3, 'https://lh3.googleusercontent.com/proxy/ff9dk6oZrpXnFAOf5w3ZZORiJvBNtaegnqu0fu_e6j2xa18hFIyPByf4ybLALbiWi37M0mjrDm5EtoePgZsB3I8Mvo8yu6I', 'https://lh3.googleusercontent.com/proxy/ff9dk6oZrpXnFAOf5w3ZZORiJvBNtaegnqu0fu_e6j2xa18hFIyPByf4ybLALbiWi37M0mjrDm5EtoePgZsB3I8Mvo8yu6I', 'https://lh3.googleusercontent.com/proxy/ff9dk6oZrpXnFAOf5w3ZZORiJvBNtaegnqu0fu_e6j2xa18hFIyPByf4ybLALbiWi37M0mjrDm5EtoePgZsB3I8Mvo8yu6I', 'https://lh3.googleusercontent.com/proxy/ff9dk6oZrpXnFAOf5w3ZZORiJvBNtaegnqu0fu_e6j2xa18hFIyPByf4ybLALbiWi37M0mjrDm5EtoePgZsB3I8Mvo8yu6I', 0, 0),
(33, 'Apple Watch SE GPS + Cellular 44mm', '10999000', 'Đánh giá chi tiết Apple Watch SE GPS + Cellular 44mm viền nhôm dây cao su\r\nTích hợp eSIM và kết nối mạng 4G LTE, đồng hồ Apple Watch SE có thể hoạt động độc lập, liên lạc, vào mạng mà không cần điện thoại ở bên. Chưa hết, thiết kế ấn tượng, cấu hình mạnh mẽ và màn hình Retina sắc nét tạo nên đẳng cấp riêng biệt cho Apple Watch SE.\r\nTích hợp eSIM, kết nối độc lập\r\nVới việc hỗ trợ eSIM, Apple Watch SE GPS + Cellular 44mm có thể hoạt động, kết nối độc lập. Thoải mái nghe gọi đàm thoại, trả lời tin nhắn, vào mạng mà không cần điện thoại bên cạnh. Bạn sẽ có thể làm nhiều điều hơn trên chiếc Apple Watch của mình.Vẻ đẹp của công nghệ\r\nPhiên bản Apple Watch SE trong bài có viền nhôm bóng bẩy và dây cao su khỏe khoắn, thời trang. Bạn sẽ có cảm giác đeo thoải mái, mặt đồng hồ trẻ trung, màn hình Retina viền mỏng tuyệt đẹp, cho một thiết kế trẻ trung, năng động. Mặt đồng hồ lớn 44mm và màu đen mạnh mẽ thích hợp hơn với nam giới.', 11, 'https://images.fpt.shop/unsafe/fit-in/665x374/filters:quality(100):fill(white)/fptshop.com.vn/Uploads/Originals/2020/9/22/637363884879247780_apple-watch-se-black-3.jpg', 'https://images.fpt.shop/unsafe/fit-in/665x374/filters:quality(100):fill(white)/fptshop.com.vn/Uploads/Originals/2020/9/22/637363884879247780_apple-watch-se-black-5.jpg', 'https://images.fpt.shop/unsafe/fit-in/665x374/filters:quality(100):fill(white)/fptshop.com.vn/Uploads/Originals/2020/9/22/637363884879091394_apple-watch-se-black-16.jpg', 'https://fptshop.com.vn/Uploads/images/2015/Tin-Tuc/QuanLNH2/apple-watch-se-black-10.jpg', 0, 0),
(34, 'iMac 24 inch 4.5K - 2021 - Apple M1【8 Core CPU | 7 Core GPU】 16GB 512GB SSD', '43990000', 'Được ra mắt vào tháng 4 năm 2021, IMac M1 24 inch chắc chắn sẽ là lựa chọn của đại đa số người dùng, nó cung cấp quá đủ tính năng quan trọng hàng ngày như màn hình chất lượng cao, Camera, Micro và hệ thống loa như Studio chuyên nghiệp. Thay vào sử dụng thế hệ chip Intel như trước đây, nay Apple đã chính thức sử dụng cây nhà trông được Apple M1 Chip và sản phẩm iMac mới nhất của mình.', 3, 'https://cdn.tgdd.vn/hoi-dap/1345950/apple-imac-la-gi-man-tro-lai-cua-imac-m1-voi-thiet-ke-mau-02-800x500.jpg', 'https://cdn.tgdd.vn/hoi-dap/1345950/apple-imac-la-gi-man-tro-lai-cua-imac-m1-voi-thiet-ke-mau-02-800x500.jpg', 'https://cdn.tgdd.vn/hoi-dap/1345950/apple-imac-la-gi-man-tro-lai-cua-imac-m1-voi-thiet-ke-mau-02-800x500.jpg', 'https://cdn.tgdd.vn/hoi-dap/1345950/apple-imac-la-gi-man-tro-lai-cua-imac-m1-voi-thiet-ke-mau-02-800x500.jpg', 0, 0),
(35, 'iMac 24 inch 4.5K - 2021 - Apple M1 【 8 Core CPU | 7 Core GPU 】 8GB 256GB SSD', '34900000', 'Được ra mắt vào tháng 4 năm 2021, IMac M1 24 inch chắc chắn sẽ là lựa chọn của đại đa số người dùng, nó cung cấp quá đủ tính năng quan trọng hàng ngày như màn hình chất lượng cao, Camera, Micro và hệ thống loa như Studio chuyên nghiệp. Thay vào sử dụng thế hệ chip Intel như trước đây, nay Apple đã chính thức sử dụng cây nhà trông được Apple M1 Chip và sản phẩm iMac mới nhất của mình.', 3, 'https://24hstore.vn/upload_images/images/2020/08/25/danh-gia-apple-imac-27-inch-chuyen-nghiep-hon-bao-gio-het-1.jpg', 'https://24hstore.vn/upload_images/images/2020/08/25/danh-gia-apple-imac-27-inch-chuyen-nghiep-hon-bao-gio-het-1.jpg', 'https://24hstore.vn/upload_images/images/2020/08/25/danh-gia-apple-imac-27-inch-chuyen-nghiep-hon-bao-gio-het-1.jpg', 'https://24hstore.vn/upload_images/images/2020/08/25/danh-gia-apple-imac-27-inch-chuyen-nghiep-hon-bao-gio-het-1.jpg', 0, 0),
(36, 'Apple Watch Series 3 GPS 42mm', '5999000', 'Apple Watch Series 3 GPS 42 mm là chiếc đồng hồ thông minh mang thương hiệu Apple, sở hữu một thiết kế hiện đại và trẻ trung, phù hợp cho các hoạt động thể thao hay đi picnic. Bạn sẽ phải ngạc nhiên trước những tính năng mà chiếc đồng hồ này đang sở hữu.\r\n\r\nThiết kế đơn giản, tinh tế\r\nTương đồng với phiên bản trước, Apple Watch Series 3 GPS 42 mm được thiết kế với mặt kính được đẩy lên cao, kiểu dáng đơn giản hiện đại. Phần mặt trên của máy được chế tác bằng chất liệu nhôm, mạ bạc tỉ mỉ, trông rất tinh tế và cứng cáp. Phần dây đeo của đồng hồ, được làm bằng cao su flo-carbon, có bề mặt sắc sảo, độ bền cao, ít bị rít khi tiếp xúc với nước.\r\nMàn hình hiển thị tốt\r\nApple Watch Series 3 được trang bị màn hình cảm ứng 1.65 inch, mặt đồng hồ cảm ứng trang bị mặt kính cường lực Sapphire crystal glass giúp bảo vệ mặt kính chống trầy xước tốt và hiển thị tốt dù là dưới ánh mặt trời.', 12, 'https://images.fpt.shop/unsafe/fit-in/665x374/filters:quality(100):fill(white)/fptshop.com.vn/Uploads/Originals/2018/3/7/636560556286982373_apple-watch-series-3-gps-42mm-vien-nhom-day-mau-den1.jpg', 'https://images.fpt.shop/unsafe/fit-in/665x374/filters:quality(100):fill(white)/fptshop.com.vn/Uploads/Originals/2018/3/7/636560556288076357_apple-watch-series-3-gps-42mm-vien-nhom-day-mau-den2.jpg', 'https://images.fpt.shop/unsafe/fit-in/665x374/filters:quality(100):fill(white)/fptshop.com.vn/Uploads/Originals/2018/3/7/636560556288388349_apple-watch-series-3-gps-42mm-vien-nhom-day-mau-den5.jpg', 'https://images.fpt.shop/unsafe/fit-in/665x374/filters:quality(100):fill(white)/fptshop.com.vn/Uploads/Originals/2018/3/7/636560556288388349_apple-watch-series-3-gps-42mm-vien-nhom-day-mau-den4.jpg', 0, 0),
(37, 'iPad Mini 4 | 16GB Wifi & 4G (Likenew)', '5890000', 'iPad Mini 4 16GB Wifi & 4G là chiếc tablet nhỏ gọn và đáng mua nhất ở thời điểm hiện tại. Máy có thiết kế sang trọng, mạnh mẽ với vi xử lý Apple A8, hỗ trợ kết nối mạng LTE (4G) tiện lợi, dung lượng pin 5214 mAh ấn tượng.', 6, 'https://didongviet.vn/pub/media/catalog/product//i/p/ipad-mini-6-4g-16gb-didongviet.jpg', 'https://didongviet.vn/pub/media/catalog/product//i/p/ipad-mini-6-4g-16gb-didongviet.jpg', 'https://didongviet.vn/pub/media/catalog/product//i/p/ipad-mini-6-4g-16gb-didongviet.jpg', 'https://didongviet.vn/pub/media/catalog/product//i/p/ipad-mini-6-4g-16gb-didongviet.jpg', 0, 0),
(38, 'Apple Watch Series 3 GPS 38mm', '4999000', 'Apple Watch Series 3 GPS 38mm viền nhôm dây cao su trắng – chiếc đồng hồ mang thương hiệu Apple, sở hữu một thiết kế hiện đại và trẻ trung, phù hợp cho các hoạt động thể thao hay đi picnic. Bạn sẽ phải ngạc nhiên trước những tính năng mà chiếc đồng hồ này đang sở hữu.\r\nTương đồng với phiên bản trước, Apple Watch Series 3 được thiết kế với mặt kính được đẩy lên cao và có phần thân máy dày. Phần mặt trên của máy được chế tác bằng chất liệu nhôm, mạ bạc tỉ mỉ, trông rất tinh tế và cứng cáp.\r\n\r\nPhần dây đeo của đồng hồ thông minh này được làm bằng cao su flo-carbon, có bề mặt sắc sảo, độ bền cao, ít bị rít khi tiếp xúc với nước. Bên cạnh đó, máy được trang bị 2 hệ thống loa có khả năng chống nước, cùng bộ đo cảm biến nhịp tim đặt bên mặt dưới đồng hồ.Ở phiên bản này, Apple Watch Series 3 GPS 38mm sử dụng chip xử lý lõi kép mang tên S3 2 nhân, nhanh hơn 0.7 lần so với phiên bản cũ, cùng với GPU mới hơn, cung cấp một hiệu năng mãnh mẽ hơn so với các thế hệ trước, bạn có thể mở ứng dụng Night Sky Scan ở tốc độ hình ảnh 60 fps một cách ổn định.', 12, 'https://fptshop.com.vn/Uploads/images/2015/SANPHAM/MinhtriBin/apple-watch-series-3-gps-38mm-silver-aluminium-case-with-fog-sport-band/apple-watch-series-3-gps-38mm-silver-aluminium-case-with-fog-sport-band1.jpg', 'https://fptshop.com.vn/Uploads/images/2015/SANPHAM/MinhtriBin/apple-watch-series-3-gps-38mm-silver-aluminium-case-with-fog-sport-band/apple-watch-series-3-gps-38mm-silver-aluminium-case-with-fog-sport-band2.jpg', 'https://fptshop.com.vn/Uploads/images/2015/SANPHAM/MinhtriBin/apple-watch-series-3-gps-38mm-silver-aluminium-case-with-fog-sport-band/apple-watch-series-3-gps-38mm-silver-aluminium-case-with-fog-sport-band3.jpeg', 'https://fptshop.com.vn/Uploads/images/2015/SANPHAM/MinhtriBin/apple-watch-series-3-gps-38mm-silver-aluminium-case-with-fog-sport-band/apple-watch-series-3-gps-38mm-silver-aluminium-case-with-fog-sport-band5.jpg', 0, 0),
(39, 'iPad Gen 5 2017 | 32GB Wifi & 4G', '7290000', 'iPad 9.7 inch 2017 32GB Wifi & 4G (Like New) thiết kế mỏng nhẹ và màn hình rộng rãi 9.7 inches, RAM 2GB, hoạt động mượt mà.<br> iPad 9.7 inch 2017 32GB Wifi & 4G Like New được bán tại Di Động Việt có ngoại hình đẹp như mới, máy đã qua kiểm duyệt gắt gao về chất lượng, trải nghiệm nên đảm bảo sự ổn đinh khi sử dụng, đồng thời nhận đủ đặc quyền, ưu đãi và khuyến mãi hấp dẫn tại Di Động Việt.', 4, 'https://didongviet.vn/pub/media/catalog/product//i/p/ipad-9-7inch-2017-32gb-wifi-4g-didongviet_3.jpg', 'https://didongviet.vn/pub/media/catalog/product//i/p/ipad-9-7inch-2017-32gb-wifi-4g-didongviet_3.jpg', 'https://didongviet.vn/pub/media/catalog/product//i/p/ipad-9-7inch-2017-32gb-wifi-4g-didongviet_3.jpg', 'https://didongviet.vn/pub/media/catalog/product//i/p/ipad-9-7inch-2017-32gb-wifi-4g-didongviet_3.jpg', 0, 0),
(40, 'iPad Air 2 16GB Wifi & 4G', '6390000', 'iPad Air 2 16GB Wifi & 4G là hàng xách tay chính hãng Apple. Máy nổi bật với ngoại hình sang trọng, hình thức còn như mới 99%, không trầy xước. Máy đã qua kiểm duyệt về chất lượng và cho trải nghiệm cao cấp đáng giá với hiệu năng mạnh mẽ, pin ổn định, nhiều tính năng cao cấp hỗ trợ tốt cho công việc lẫn giải trí.', 4, 'https://didongviet.vn/pub/media/catalog/product//i/p/ipad-air-2-4g-16gb-didongviet.jpg', 'https://didongviet.vn/pub/media/catalog/product//i/p/ipad-air-2-didongviet_6.jpg', 'https://didongviet.vn/pub/media/catalog/product//i/p/ipad-air-2-gia-bao-nhieu-didongviet.jpg', 'https://didongviet.vn/pub/media/catalog/product//i/p/ipad-air-2-4g-16gb-didongviet.jpg', 0, 0),
(41, 'Apple Watch Series 3 42 mm LTE', '4190000', 'Apple Watch Series 3 42 mm LTE cũ xét về thiết kế không có quá nhiều thay đổi so với đời 2 hay các đời trước đó nên các phụ kiện vẫn dùng chung được. Bản LTE khác với bản thường trong thiết kể bởi cái vòng điều khiển Digital Crown sơn màu đỏ.\r\n\r\nĐây cũng chính là điểm nhận dạng để người dùng phân biệt các phiên bản Apple Watch. \r\nBản LTE của Apple Watch Series 3 42 mm cũ sẽ có bộ nhớ trong 16GB thay vì chỉ 8GB như bản thường. ', 12, 'https://www.duchuymobile.com/images/detailed/26/anh-slider-apple-watch-series-3-so-luong_47fd-wv.jpg', 'https://www.duchuymobile.com/images/detailed/26/anh-slider-apple-watch-series-3-tren-tay_j062-sb.jpg', 'https://www.duchuymobile.com/images/detailed/26/anh-slider-apple-watch-series-3-mau-hong_68vg-zk.jpg', 'https://www.duchuymobile.com/images/detailed/26/anh-slider-apple-watch-series-3-dep_ge3h-ih.jpg', 0, 0),
(42, 'iPad Pro 11-inch 2021 | M1 128GB Wifi', '22990000', 'iPad Pro 2021 11inch 128GB WiFi có thiết kế tinh tế, hiện đại tích hợp với con chip Apple M1 với hiệu năng cực khủng, màn hình Liquid Retina 11inch hỗ trợ tần số quét 120Hz hứa hẹn sẽ mang lại nhiều trải nghiệm tuyệt vời. ', 5, 'https://didongviet.vn/pub/media/catalog/product//i/p/ipad-pro-2021-11inch-m1-128gb-wifi.jpg', 'https://didongviet.vn/pub/media/catalog/product//c/a/camera-sau-ipad-pro-2021-11inch-wifi-didongviet.jpg', 'https://didongviet.vn/pub/media/catalog/product//i/p/ipad-pro-2021-11inch-m1-128gb-wifi.jpg', 'https://didongviet.vn/pub/media/catalog/product//i/p/ipad-pro-2021-11inch-m1-128gb-wifi.jpg', 0, 0),
(43, 'iPad Pro 12.9-inch 2021 | M1 128GB Wifi', '27490000', 'iPad Pro 2021 12.9inch 128GB Wifi với thiết kế màn hình với viền bezel mỏng, trang bị màn hình Mini-LED cho model 12.9 inch với tỉ lệ tương phản 1.000.000:1 với nhiều cải tiến mới so với màn hình iPad hiện tại.Cấu hình mạnh mẽ với chip M1 với 8 nhân CPU và 8 nhân GPU, với con Chip ấn tượng có thể xử lý tốt những tác vụ thường ngày một cách mượt mà.', 5, 'https://didongviet.vn/pub/media/catalog/product//i/p/ipad-pro-m1-2021-chinh-hang-didongviet.jpg', 'https://didongviet.vn/pub/media/catalog/product//m/u/mua-ipad-pro-m1-2021-didongviet.jpg', 'https://didongviet.vn/pub/media/catalog/product//i/p/ipad-pro-m1-2021-didongviet_2.jpg', 'https://didongviet.vn/pub/media/catalog/product//i/p/ipad-pro-m1-2021-chinh-hang-didongviet.jpg', 0, 0),
(44, 'Apple Watch SE Nike - 44mm - GPS', '7890000', 'Apple Watch SE Nike với tính năng chăm sóc sức khoẻ tuyệt vời kèm theo đó là thiết kế dây thể thao từ Nike phá cách mang đậm chất thời trang, đây là sản phẩm hàng đầu dành cho người yêu thích thể thao.\r\nApple Watch SE Nike có gì khác biệt?\r\nApple Watch SE Nike là một sản phẩm smartwatch của Apple nhưng có nhiều cải tiến cả về hình thức lẫn chất lượng, mang dấu ấn của hãng thời trang Nike. Điểm đặc biệt ở Watch SE Nike mới này chính là phần dây đeo đồng hồ sẽ được thiết kế theo phong cách Nike, phá vỡ mọi giới hạn.\r\n\r\nĐây là chiếc đồng hồ thông minh được đánh giá là rất phù hợp với người yêu thích thể thao và rèn luyện sức khoẻ là thói quen hàng ngày.\r\n\r\nApple Watch SE Nike - Lựa chọn hàng đầu cho những người yêu thể thao\r\nApple Watch SE Nike 44mm, GPS, mặt nhôm, dây cao su là sự kết hợp táo bạo của Apple với thương hiệu thời trang nổi tiếng Nike. Với thiết kế dây đeo đậm chất thể thao và ứng dụng Nike Run Club hữu ích, phiên bản Nike của dòng Watch SE chắn chắn sẽ mang đến trải nghiệm tuyệt vời cho người đam mê và yêu thích thể thao.', 13, 'https://24hstore.vn/upload_images/images/2020/09/30/apple-watch-se-nike-44mm-gps-day-cao-su-h33.jpg', 'https://24hstore.vn/upload_images/images/2020/09/30/apple-watch-se-nike-44mm-gps-day-cao-su-h11.jpg', 'https://24hstore.vn/upload_images/images/2020/09/30/apple-watch-se-nike-44mm-gps-day-cao-su-h44.jpg', 'https://24hstore.vn/upload_images/images/2020/09/30/apple-watch-se-nike-44mm-gps-day-cao-su-h11(1).jpg', 0, 0),
(45, 'iPad Pro 11-inch 2021 | M1 512GB Wifi & 5G', '35990000', 'iPad Pro 2021 11inch 512GB WiFi & 5G có thiết kế tinh tế, hiện đại tích hợp với con chip Apple M1 với hiệu năng cực khủng, màn hình Liquid Retina 11inch hỗ trợ tần số quét 120Hz hứa hẹn sẽ mang lại nhiều trải nghiệm tuyệt vời. ', 5, 'https://didongviet.vn/pub/media/catalog/product//m/u/mua-apple-ipad-pro-m1-2021-didongviet_1.jpg', 'https://didongviet.vn/pub/media/catalog/product//i/p/ipad-pro-m1-2021-didongviet_1.jpg', 'https://didongviet.vn/pub/media/catalog/product//g/i/gia-apple-ipad-pro-m1-2021-didongviet_2.jpg', 'https://didongviet.vn/pub/media/catalog/product//m/u/mua-apple-ipad-pro-m1-2021-didongviet_1.jpg', 0, 0),
(46, 'iPad Mini 6 2021 | Wifi & 5G 64GB', '20590000', 'iPad Mini 6 64GB Wifi & 5G được trang bị con chip A15 Bionic mạnh mẽ, thiết kế mỏng nhẹ và màn hình lớn 8.3 inch phù hợp với người dùng cần trải nghiệm tiện lợi, mượt mà trên một chiếc tablet thương hiệu lớn.\r\n\r\niPad Mini 6 64GB Wifi & 5G được bán tại Di Động Việt được bảo hành 12 tháng theo chính sách của Apple toàn cầu. Ngoài ra, khách hàng sẽ được hưởng thêm nhiều đặc quyền, ưu đãi và khuyến mãi hấp dẫn khác khi mua máy tại Di Động Việt.', 6, 'https://didongviet.vn/pub/media/catalog/product//i/p/ipad-mini-6-64gb-wifi-5g.jpg', 'https://didongviet.vn/pub/media/catalog/product//i/p/ipad-mini-6-3-didongviet_1_2.jpg', 'https://didongviet.vn/pub/media/catalog/product//i/p/ipad-mini-6-5-didongviet_1_2.jpg', 'https://didongviet.vn/pub/media/catalog/product//i/p/ipad-mini-6-64gb-wifi-5g.jpg', 0, 0),
(47, 'Apple Watch Nike + GPS Series 4 44mm Silver Aluminum Case', '12489000', 'Sản phẩm Tân trang được Chứng nhận này đã được kiểm tra và chứng nhận hoạt động và trông giống như mới, ít hoặc không có dấu hiệu mài mòn, bởi người bán bên thứ ba chuyên biệt được Amazon phê duyệt. Sản phẩm được bảo hành tối thiểu 90 ngày và có thể đến trong hộp màu nâu hoặc trắng thông thường. Các phụ kiện có thể là chung và không phải trực tiếp từ nhà sản xuất. • Dòng 3, tích hợp GPS và SIM điện tử - luôn kết nối khi bỏ quên điện thoại • Thực hiện cuộc gọi, gửi tin nhắn, phát trực tuyến nhạc, đặt lời nhắc và nhận thông báo từ ứng dụng yêu thích của bạn – tất cả chỉ với đồng hồ của bạn. • Chống nước lên đến 50M - có thể được sử dụng cho các hoạt động ở vùng nước nông như bơi trong hồ bơi hoặc đại dương • Apple Watch Series 3 (GPS + Cellular) yêu cầu iPhone 6 trở lên chạy iOS 11 trở lên. Nhà cung cấp dịch vụ Apple Watch và iPhone phải giống nhau', 13, 'https://store.storeimages.cdn-apple.com/4981/as-images.apple.com/is/image/AppleInc/aos/published/images/4/4/44/aluminum/44-aluminum-silver-case?wid=196&hei=372&fmt=jpeg&qlt=95&op_usm=0.5,0.5&.v=1535393300683', 'https://store.storeimages.cdn-apple.com/4981/as-images.apple.com/is/image/AppleInc/aos/published/images/w/at/watch/witb/watch-witb-plug-201809?wid=196&hei=372&fmt=jpeg&qlt=95&op_usm=0.5,0.5&.v=1535393314298', 'https://store.storeimages.cdn-apple.com/4981/as-images.apple.com/is/image/AppleInc/aos/published/images/4/4/44/aluminum/44-aluminum-silver-case?wid=196&hei=372&fmt=jpeg&qlt=95&op_usm=0.5,0.5&.v=1535393300683', 'https://store.storeimages.cdn-apple.com/4981/as-images.apple.com/is/image/AppleInc/aos/published/images/w/at/watch/witb/watch-witb-charger-201809?wid=196&hei=372&fmt=jpeg&qlt=95&op_usm=0.5,0.5&.v=1535393313564', 0, 0),
(48, 'iPad Mini 3 16GB Wifi & 4G', '4190000', 'iPad Mini 3 16GB Wifi & 4G cũ có thiết kế nhỏ gọn, màn hình 7.9 inches, camera sau 5MP, dung lượng pin 6470 mAh cho thời gian sử dụng lâu dài.\r\n\r\niPad Mini 3 16GB Wifi & 4G cũ bán tại Di Động Việt có ngoại hình rất mới, được cam kết nguyên bản chưa qua sửa chữa, chất lượng đã được kiểm định kĩ càng. Máy được bảo hành trong 6 tháng và nhận đủ các ưu đãi, đặc quyền khuyến mãi hấp dẫn tại Di Động Việt.', 6, 'https://didongviet.vn/pub/media/catalog/product//i/p/ipad-mini-3-16gb-wifi-4g-trang-didongviet_1_1.jpg', 'https://didongviet.vn/pub/media/catalog/product//i/p/ipad-mini-3-16gb-wifi-4g-trang-didongviet_1_1.jpg', 'https://didongviet.vn/pub/media/catalog/product//i/p/ipad-mini-3-16gb-wifi-4g-trang-didongviet_1_1.jpg', 'https://didongviet.vn/pub/media/catalog/product//i/p/ipad-mini-3-16gb-wifi-4g-trang-didongviet_1_1.jpg', 0, 0),
(49, 'Apple Watch S6 Nike Space Gray Aluminum Case', '13550000', 'Tại sự kiện \"Time Flies\" được tổ chức vào lúc rạng sáng ngày 16/9 (theo giờ Việt Nam), Apple đã chính thức giới thiệu thế hệ Apple Watch tiếp theo của mình: Apple Watch Series 6 là một phiên bản nâng cấp tương đối nhẹ của chiếc Series 5 ra mắt hồi năm ngoái.\r\n\r\nThiết kế không đổi\r\n\r\nNếu bạn mong chờ vào một thiết kế mới trên Apple Watch thì có lẽ bạn sẽ phải thất vọng bởi Series 6 năm nay có thiết kế không thay đổi quá nhiều so với Series 5, hay thậm chí là Series 4. Điều khác biệt duy nhất có thể nhận thấy là các cảm biến đo nồng độ oxy trong máu mới được đặt dưới đáy của Apple Watch Series 6.Series 6 sử dụng màn hình LTPO Retina tương tự so với thế hệ tiền nhiệm, hỗ trợ tính năng Alway-on Display. Người dùng sẽ vẫn có hai phiên bản kích thước là 40mm và 44mm.\r\n\r\nTính năng mới: Đo nồng độ oxy trong máu (SpO2)\r\n\r\nNhư đã đề cập ở trên, Apple Watch Series 6 được trang bị các cảm biến quang học mới cho phép đo độ bão hòa oxy trong máu của người dùng. Tính năng này khá hữu ích trong thời điểm dịch bệnh COVID-19 đang trở nên cấp báo hơn bởi nồng độ oxy trong máu thấp có thể là dấu hiệu cho thấy bạn đã dương tính với virus corona chủng mới.\r\n\r\n ', 13, 'https://product.hstatic.net/1000300544/product/mx8c2_vw_34fr_watch-40-alum-spacegray-cell-nike6s_vw_34fr_wf_co_711f323b97f64465ae6f79f675bee07a_grande.jpeg', 'https://product.hstatic.net/1000300544/product/mx8c2_vw_pf_watch-40-alum-spacegray-nc-nike6s_vw_pf_wf_co_96d50b3a3cb2443596af544496583b55_grande.jpeg', 'https://product.hstatic.net/1000300544/product/mg3v3_vw_34fr_watch-40-alum-spacegray-cell-nike6s_vw_34fr_wf_co_170235937d664b1fb7aa62031fa4d180_grande.jpeg', 'https://product.hstatic.net/1000300544/product/mx8c2_vw_34fr_watch-40-alum-spacegray-cell-nike6s_vw_34fr_wf_co_711f323b97f64465ae6f79f675bee07a_grande.jpeg', 0, 0),
(50, 'iPhone 13 128GB Chính Hãng', '24990000', 'iPhone 13 128GB Chính hãng (VN/A) bán tại Di Động Việt - Đại lý uỷ quyền chính thức của Apple tại Việt Nam, iPhone 13 mini là phiên bản quốc tế 2 sim (Nano + Esim) chính hãng VN/A. Máy chưa Active + nguyên seal hộp, mới 100% (Fullbox)\r\niPhone 13 128GB Chính hãng (VN/A) là phiên bản được phân phối chính thức bởi Apple Việt Nam, được bảo hành 12 tháng tại Trung tâm Uỷ quyền cao cấp nhất của Apple tại Việt Nam và trên toàn cầu miễn phí. Đồng thời hưởng nhiều ưu đãi, khuyến mãi hấp dẫn tại Di Động Việt.', 7, 'https://didongviet.vn/pub/media/catalog/product//i/p/iphone-13-black-friday_2.jpg', 'https://didongviet.vn/pub/media/catalog/product//i/p/iphone-13-red-9-didongviet.jpg', 'https://didongviet.vn/pub/media/catalog/product//i/p/iphone-13-red-10-didongviet.jpg', 'https://didongviet.vn/pub/media/catalog/product//i/p/iphone-13-black-friday_2.jpg', 0, 0);
